public class Hello {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float p=13000.0f;
		float t=2.0f;
		float r=9.0f;
		float sI;
		sI=(p*r*t)/100;
		System.out.println("SI:"+sI);
		float totalAmount;
		totalAmount=p*(1+r*t);
		System.out.println("total amount:"+totalAmount);
		

	}

}
